mapper.select:
	/* selectMember */
	SELECT
		mbr.mbr_no,
		mbr.login_id,
		mbr.sex,
		mbr.age_grp,
		co.co_no,
		co.co_nm
	FROM
		ally_mbr mbr,
		ally_co co
	WHERE
		mbr.co_no = co.co_no

    		AND mbr.mbr_no = #{memberNo}


    		AND mbr.login_id = #{loginID}


    		AND mbr.sex = #{sex}


    		AND mbr.age_grp = #{ageGroup}


    		AND mbr.co_no = #{companyNo}

	/* selectMemberCount */
	SELECT
		COUNT(*)
	FROM
		ally_mbr
	WHERE
		1=1

    		AND mbr_no = #{memberNo}


    		AND login_id = #{loginID}


    		AND sex = #{sex}


    		AND age_grp = #{ageGroup}


    		AND co_no = #{companyNo}

/* selectMemberEmailCertificationInfo */ SELECT email_addr, crt_cd, crt_cd_isue_ymdt FROM ally_mbr_email_crt WHERE 1=1
AND email_addr = #{loginID}
AND crt_cd = #{certificationCode}

    	/* selectMemberEmailCertificationInfoCount */
    	SELECT
			COUNT(*)
		FROM
			ally_mbr_email_crt
		WHERE
			1=1

	    		AND email_addr = #{loginID}


	    		AND crt_cd = #{certificationCode}
